import React, { useState, useEffect } from "react";
import { Select, Spin, Button, Input, TimePicker, Upload } from "antd";
import { connect } from "react-redux";
import styles from "./VenueInformation.module.css";
import axios from "../../../axios";
import * as actions from "../../../store/actions/index";
import moment from "moment";
import Map from "../../Map/Map";

const format = "HH:mm";
const { TextArea } = Input;
const { Option } = Select;

const props = {
    name: 'file',
    action: 'https://www.mocky.io/v2/5cc8019d300000980a055e76',
    headers: {
      authorization: 'authorization-text',
    },
}

function VenueInformation({ user, loc, onSubmit, google }) {
    const [data, setData] = useState([]);
    const [fetching, setFetching] = useState(false);
    const [value, setValue] = useState([]);
    const [facilityValue, setFacilityValue] = useState([]);
    const [facilities, setFacility] = useState([]);
    const [vName, setVName] = useState('');
    const [vDes, setVDes] = useState('');
    const [vPic, setVPic] = useState(null);

    const fetchSports = value => {
        setFetching(true);
        const data = {
            name: value
        };

        axios.post("/sport/search", data).then(res => {
            setData(res.data.sports);
            setFetching(false);
        });
    };

    const fetchFacility = value => {
        setFetching(true);
        const data = {
            name: value
        };

        axios.post("/facility/search", data).then(res => {
            setFacility(res.data.facilities);
            setFetching(false);
        });
    };

    useEffect(() => {
        fetchSports("");
        fetchFacility("");
    }, []);

    const submitHandler = async () => {
        const data = {
            venue: {
                userId: user.id,
                name: vName,
                address: loc.address,
                city: loc.city,
                description: vDes,
                latitude: loc.lat,
                longitude: loc.long,
                photos: vPic
            }
        }

        axios.post('/venue/upsert')

        onSubmit();
    };

    const changeHandler = value => {
        setValue(value);
        setData([]);
        setFetching(false);
    };

    const changeHandlerFacility = value => {
        setFacilityValue(value);
        setFacility([]);
        setFetching(false);
    };

    const setVenuePic = value => {
        setVPic(value.file.name);
    }

    return (
        <>
            <div className={styles.form_item}>
                <div className={styles.form_label}>Venue Name</div>
                <Input className={styles.form_input} value={vName} onChange={(e) => setVName(e.target.value)} />
            </div>
            <div className={styles.form_item}>
                <Map
                    google={google}
                    center={{ lat: -6.2572006, lng: 106.7913482 }}
                    height="300px"
                    zoom={15}
                />
            </div>
            <br />
            <br />
            <div className={styles.form_item}>
                <div className={styles.form_label}>Venue Description</div>
                <TextArea rows={4} className={styles.form_input} value={vDes} onChange={(e) => setVDes(e.target.value)} />
            </div>
            <div className={styles.form_item}>
                <div className={styles.form_label}>Venue Picture</div>
                        <Upload {...props} onChange={setVenuePic}>
                            <Button>
                            <Icon type="upload" /> Click to Upload
                            </Button>
                        </Upload>
            </div>
            <div className={styles.form_item}>
                <div className={styles.form_label}>
                    Which Sport does the venue accomodate ?
                </div>
                <Select
                    mode="multiple"
                    labelInValue
                    value={value}
                    placeholder="Select Sports"
                    notFoundContent={fetching ? <Spin size="small" /> : null}
                    filterOption={false}
                    onSearch={fetchSports}
                    className={styles.form_select}
                    onChange={changeHandler}
                >
                    {data.map(d => (
                        <Option key={d.id}>{d.name}</Option>
                    ))}
                </Select>
            </div>
            <div className={styles.form_item}>
                <div className={styles.form_label}>
                    What Facility does your venue offer ?
                </div>
                <Select
                    mode="multiple"
                    labelInValue
                    value={facilityValue}
                    placeholder="Select Facility"
                    notFoundContent={fetching ? <Spin size="small" /> : null}
                    filterOption={false}
                    onSearch={fetchFacility}
                    className={styles.form_select}
                    onChange={changeHandlerFacility}
                >
                    {facilities.map(d => (
                        <Option key={d.id}>{d.name}</Option>
                    ))}
                </Select>
            </div>
            <div className={styles.form_item}>
                <div className={styles.form_label}>
                    Set your venue opening hours
                </div>
                <div className={styles.form_row}>
                    <span className={styles.form_row_label}>Monday</span>
                    <TimePicker
                        defaultValue={moment("09:00", format)}
                        format={format}
                        placeholder={""}
                        className={styles.form_row_input}
                    />
                    &nbsp; - &nbsp;
                    <TimePicker
                        defaultValue={moment("20:00", format)}
                        format={format}
                        placeholder={""}
                        className={styles.form_row_input}
                    />
                </div>
                <div className={styles.form_row}>
                    <span className={styles.form_row_label}>Tuesday</span>
                    <TimePicker
                        defaultValue={moment("09:00", format)}
                        format={format}
                        placeholder={""}
                        className={styles.form_row_input}
                    />
                    &nbsp; - &nbsp;
                    <TimePicker
                        defaultValue={moment("20:00", format)}
                        format={format}
                        placeholder={""}
                        className={styles.form_row_input}
                    />
                </div>
                <div className={styles.form_row}>
                    <span className={styles.form_row_label}>Wednesday</span>
                    <TimePicker
                        defaultValue={moment("09:00", format)}
                        format={format}
                        placeholder={""}
                        className={styles.form_row_input}
                    />
                    &nbsp; - &nbsp;
                    <TimePicker
                        defaultValue={moment("20:00", format)}
                        format={format}
                        placeholder={""}
                        className={styles.form_row_input}
                    />
                </div>
                <div className={styles.form_row}>
                    <span className={styles.form_row_label}>Thursday</span>
                    <TimePicker
                        defaultValue={moment("09:00", format)}
                        format={format}
                        placeholder={""}
                        className={styles.form_row_input}
                    />
                    &nbsp; - &nbsp;
                    <TimePicker
                        defaultValue={moment("20:00", format)}
                        format={format}
                        placeholder={""}
                        className={styles.form_row_input}
                    />
                </div>
                <div className={styles.form_row}>
                    <span className={styles.form_row_label}>Friday</span>
                    <TimePicker
                        defaultValue={moment("09:00", format)}
                        format={format}
                        placeholder={""}
                        className={styles.form_row_input}
                    />
                    &nbsp; - &nbsp;
                    <TimePicker
                        defaultValue={moment("20:00", format)}
                        format={format}
                        placeholder={""}
                        className={styles.form_row_input}
                    />
                </div>
                <div className={styles.form_row}>
                    <span className={styles.form_row_label}>Saturday</span>
                    <TimePicker
                        defaultValue={moment("09:00", format)}
                        format={format}
                        placeholder={""}
                        className={styles.form_row_input}
                    />
                    &nbsp; - &nbsp;
                    <TimePicker
                        defaultValue={moment("20:00", format)}
                        format={format}
                        placeholder={""}
                        className={styles.form_row_input}
                    />
                </div>
                <div className={styles.form_row}>
                    <span className={styles.form_row_label}>Sunday</span>
                    <TimePicker
                        defaultValue={moment("09:00", format)}
                        format={format}
                        placeholder={""}
                        className={styles.form_row_input}
                    />
                    &nbsp; - &nbsp;
                    <TimePicker
                        defaultValue={moment("20:00", format)}
                        format={format}
                        placeholder={""}
                        className={styles.form_row_input}
                    />
                </div>
            </div>
            <div className={styles.form_item}>
                <Button type="primary" onClick={submitHandler}>
                    Submit
                </Button>
            </div>
        </>
    );
}

const mapStateToProps = state => {
    return {
        user: state.authReducer.user,
        loc: state.preferenceReducer.location
    };
};

const mapDispatchToProps = dispatch => {
    return {
        onSubmit: () => dispatch(actions.updateCurrPref())
    };
};

export default connect(
    mapStateToProps,
    mapDispatchToProps
)(VenueInformation);
