import React from "react";
import SliderDiv from "react-slick";

import { Row, Col } from "antd";
import CardItem from "../CardItem/CardItem";

const defaultShow = 6;

function Slider({ data }) {
    const show = data.length < defaultShow ? data.length : defaultShow;

    const settings = {
        dots: false,
        infinite: true,
        speed: 500,
        slidesToShow: show,
        slidesToScroll: 1,
        responsive: [
            {
                breakpoint: 1500,
                settings: {
                    slidesToShow: show
                }
            },
            {
                breakpoint: 1200,
                settings: {
                    slidesToShow: show
                }
            },
            {
                breakpoint: 992,
                settings: {
                    slidesToShow: show
                }
            },
            {
                breakpoint: 768,
                settings: {
                    slidesToShow: show
                }
            },
            {
                breakpoint: 575,
                settings: {
                    slidesToShow: 1
                }
            }
        ]
    };

    console.log(data);

    return (
        <SliderDiv {...settings} style={{ display: "flex" }}>
            {data.map((item, index) => {
                if (item === undefined && data.every(el => el === undefined)) {
                    return (
                        <Row gutter={16} key={index}>
                            <Col span={24}>
                                <CardItem item="Temp" loading={true} />
                            </Col>
                        </Row>
                    );
                }

                return (
                    <Row gutter={16} key={item.id}>
                        <Col span={24}>
                            <CardItem item={item} loading={false} />
                        </Col>
                    </Row>
                );
            })}
        </SliderDiv>
    );
}

export default Slider;
