import React, { useEffect } from "react";

function Modal({ type, msg, callback }) {
    useEffect(() => {
        if (type === "error") {
            modal("error", "Error", msg, () => {
                if (typeof callback === 'function') {
                    callback();
                }
            });
        }
        else if(type === 'success') {
            modal("success", "Success", msg, () => {
                if (typeof callback === 'function') {
                    callback();
                }
            });
        }
    });
}

export default Modal;
