import React, { useState, useEffect } from "react";
import { Select, Spin, Button } from "antd";
import { connect } from "react-redux";

import axios from "../../../axios";
import * as actions from "../../../store/actions/index";

const { Option } = Select;

function Sport({ user }) {
    const [data, setData] = useState([]);
    const [fetching, setFetching] = useState(false);
    const [value, setValue] = useState([]);

    const fetchSports = value => {
        setFetching(true);
        const data = {
            name: value
        }

        axios.post('/sport/search', data).then(res => {
            setData(res.data.sports);
            setFetching(false);
        });
    };

    useEffect(() => {
        fetchSports('');
    }, []);

    const submitHandler = async () => {
        await value.map(item => {
            const data = {
                userSportMapping: item.id,
                userId: user.id
            }

            axios.post('/user/sport/mapping/upsert', data);
        });

        updateCurrPref();
    }

    const changeHandler = value => {
        setValue(value);
        setData([]);
        setFetching(false);
    };

    return (
        <>
        <h1>What Sports do you like ? (Choose minimal one)</h1>
        <Select
            mode="multiple"
            labelInValue
            value={value}
            placeholder="Select Sports"
            notFoundContent={fetching ? <Spin size="small" /> : null}
            filterOption={false}
            onSearch={fetchSports}
            onChange={changeHandler}
            style={{ width: "50%" }}
        >
            {data.map(d => (
                <Option key={d.id}>{d.name}</Option>
            ))}
        </Select>
        <Button type="primary" onClick={submitHandler}>Submit</Button>
        </>
    );
}

const mapStateToProps = state => {
    return {
        user: state.authReducer.user
    }
}

const mapDispatchToProps = dispatch => {
    return {
        onSubmit: () => dispatch(actions.updateCurrPref())
    }
}

export default connect(mapStateToProps)(Sport);
