import React, { useState, useEffect } from "react";
import { Select, Spin, Button } from "antd";
import { connect } from "react-redux";

import axios from "../../../axios";

const { Option } = Select;

function Sport() {
    const [data, setData] = useState([]);
    const [fetching, setFetching] = useState(false);
    const [value, setValue] = useState([]);

    const fetchSports = value => {
        setFetching(true);
        const data = {
            name: value
        }

        axios.post('/sport/search', data).then(res => {
            setData(res.data.sports);
            setFetching(false);
        });
    };

    useEffect(() => {
        fetchSports('');
    }, []);

    const submitHandler = () => {
        value.map(item => {
            const data = {
                userSportMapping: item
            }
            axios.post('/user/sport/mapping/upsert', )
        });
    }

    const changeHandler = value => {
        setValue(value);
        setData([]);
        setFetching(false);
    };

    return (
        <>
        <h1>What Sports do you like ? (Choose minimal one)</h1>
        <Select
            mode="multiple"
            labelInValue
            value={value}
            placeholder="Select Sports"
            notFoundContent={fetching ? <Spin size="small" /> : null}
            filterOption={false}
            onSearch={fetchSports}
            onChange={changeHandler}
            style={{ width: "50%" }}
        >
            {data.map(d => (
                <Option key={d.id}>{d.name}</Option>
            ))}
        </Select>
        <Button type="primary" onClick={submitHandler}>Submit</Button>
        </>
    );
}

export default Sport;
